mapper.select:
SELECT u.*,r.role_name
FROM `user` u LEFT JOIN tb_role r ON u.role_id = r.id

    1=1

        AND user_name LIKE CONCAT('%',#{user_name},'%')


        AND user_no = #{user_no}


        AND card_id = #{card_id}

mapper.update:
update  user set user_no = #{user_no}, user_name=#{user_name}, password=#{password}, sex=#{sex}, card_id=#{card_id},
 email=#{email}, tel=#{tel}, hobby=#{hobby}, role_id=#{role_id}, info=#{info}
where id =#{id}
